import './App.css';
import { useState } from 'react';
import { getRandomCode, getError} from "./game";

function YouLost({reset}) {
    return (
        <div className="App">
            <h1>Loser!</h1>
            <p>
                <button onClick={reset}>
                    New Game
                </button>
            </p>
        </div>
    );
}

function App() {

  const [code, setCode] = useState("1234");
  const [guesses, setGuesses] = useState([]);
  const [number, setNumber] = useState("");
  const [error, setError] = useState("");

  function guess(ev) {
      let g = getError(ev.target.value);
      setError(getError(g));
      if (error === "") {
          if (!guesses.includes(g)) {
              let newGuesses = guesses.concat(g);
              setGuesses(newGuesses);
          } else {
              setError("You already guessed that!");
          }
      }
  }

  function newGame() {
      setGuesses([]);
      setCode(getRandomCode());
      setNumber("");
  }

  function updateNumber(ev) {
      let d = ev.target.value;
      if (number.length < 4) {
          setNumber(number + d);
      }
  }


  if (guesses.length >= 8) {
      return <YouLost reset={newGame}/>;
  }


  return (
    <div className="App">
      <h1>4Digit Game!</h1>
        <p>
            <button onClick={newGame}>Start Over!</button>
        </p>
      <p>Enter 4 digits to make a guess:</p>
      <p>
          <input type="text" onChange={updateNumber}/>
          <button onClick={guess}>Enter</button>
      </p>
        <p>{error}

        </p>


    </div>
  );
}

export default App;
