import './App.css';
import {useState} from 'react';
import {getRandomCode, getError, getResults, userWon, getOneResult} from "./game";
import 'lodash';
import 'milligram';

function YouLost({reset}) {
    return (
        <div className="App">
            <h1>Loser!</h1>
            <p>
                <button onClick={reset}>
                    New Game
                </button>
            </p>
        </div>
    );
}

function YouWon({reset}) {
    return (
        <div className="App">
            <h1>Congrats! You Won! :)</h1>
            <p>
                <button onClick={reset}>
                    New Game
                </button>
            </p>
        </div>
    );
}

function App() {

    const [code, setCode] = useState("1234");
    const [guesses, setGuesses] = useState([]);
    const [number, setNumber] = useState("");

    let win = userWon(guesses, code);
    let results = getResults(guesses, code);
    let e = getError(number, guesses);

    function guess() {
        let n = number;
        setNumber("");
        console.log(e);
        if (e === "") {
            let newGuesses = guesses.concat(n);
            setGuesses(newGuesses);
        }
    }

    function newGame() {
        setGuesses([]);
        setCode(getRandomCode());
        setNumber("");
    }

    function updateNumber(ev) {
        if (number.length < 4) {
            let d = ev.target.value;
            let num = d[d.length - 1];
            setNumber(number + num);
        }
    }

    function keyPress(ev) {
        if (ev.key === "Enter") {
            guess();
        }
    }

    if (guesses.length >= 8) {
        return <YouLost reset={newGame}/>;
    }

    if (win) {
        return <YouWon reset={newGame}/>;
    }

    return (
        <div className="App">
            <h1>4Digit Game!</h1>
            <p>
                <button className="button-outline" onClick={newGame}>New Game</button>
            </p>
            <div className="centered">
                <p>Try to guess the secret code! Make a guess, you have eight tries to figure it out!</p>
                <p>Bulls are right numbers in the right place. Cows are right numbers in the wrong place.</p>
            </div>
            <div className="row">
                <div className="column column-50 column-offset-10 ">
                    <p>Enter 4 digits to make a guess:</p>
                    <p>
                        <input type="text" value={number} onChange={updateNumber} onKeyPress={keyPress}/>
                        <button onClick={guess}>Guess!</button>
                    </p>
                    <p>{e}</p><br/>
                </div>
                <div className="column column-25 column-offset-10 new-line">
                    {guesses.join("\n")}
                </div>
                <div className="column new-line">
                    {results.join('\n')}
                </div>
            </div>

        </div>
    );
}

export default App;
