import './App.css';
import {useState} from 'react';
import {getRandomCode, getError, getResult} from "./game";

function YouLost({reset}) {
    return (
        <div className="App">
            <h1>Loser!</h1>
            <p>
                <button onClick={reset}>
                    New Game
                </button>
            </p>
        </div>
    );
}

function YouWon({reset}) {
    return (
        <div className="App">
            <h1>Congrats! You Won! :)</h1>
            <p>
                <button onClick={reset}>
                    New Game
                </button>
            </p>
        </div>
    );
}

function App() {

    const [code, setCode] = useState("1234");
    const [guesses, setGuesses] = useState([]);
    const [number, setNumber] = useState("");
    const [error, setError] = useState("");

    function guess() {
        setError(getError(number, guesses));
        if (error === "") {
            let newGuesses = guesses.concat(number);
            setGuesses(newGuesses);
            let r = getResult(number, code);
            if (r === "4A0B") {
                return <YouWon reset={newGame}/>
            }
        }
    }

    function newGame() {
        setGuesses([]);
        setCode(getRandomCode());
        setNumber("");
    }

    function updateNumber(ev) {
        if (number.length < 4) {
            let d = ev.target.value;
            let num = d[d.length - 1];
            setNumber(number + num);
        }
    }

    if (guesses.length >= 8) {
        return <YouLost reset={newGame}/>;
    }

    return (
        <div className="App">
            <h1>4Digit Game!</h1>
            <p>
                <button onClick={newGame}>Start Over!</button>
            </p>
            <p>Enter 4 digits to make a guess:</p>
            <p>
                <input type="text" value={number} onChange={updateNumber}/>
                <button onClick={guess}>Enter</button>
            </p>
            <p>{error}</p><br/>

        </div>
    );
}

export default App;
