  }
   */

  return (
    <div className="App">
      <h1>4Digit Game!</h1>
      <p>Enter 4 digits to make a guess:</p>
      <p>
          <input type="text" value={number} onChange={setNumber} />
          <button >Enter</button>
      </p>

      <p>
          <button >Start Over!</button>
      </p>
    </div>
  );
}

export default App;
