import logo from './logo.svg';
import './App.css';
import { useState } from 'react';
import { getRandomCode, isValidGuess} from "./game";

function App() {

  const [code, setCode] = useState(getRandomCode());
  const [guesses, setGuesses] = useState([]);
  const [number, setNumber] = useState("");

  function guess() {
      if(isValidGuess(number)) {
          let newGuesses = guesses.concat(number);
          setGuesses(newGuesses);
      }
  }

  function newGame() {
      setGuesses([]);
      setCode(getRandomCode());
  }

  return (
    <div className="App">
      <h1>4Digit Game!</h1>
      <p>Enter 4 digits to make a guess:</p>
      <p>
        <input type="text" value={number} onChange={setNumber} />
        <button onClick={guess}>Enter</button>
      </p>
        <p>
            <button onClick={newGame()}>Start Over!</button>
        </p>
    </div>
  );
}

export default App;
