import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {useState} from 'react';
import {getRandomCode, getError, getResults, win} from "./game";
import 'lodash';
import 'bootstrap';

function YouLost({reset}) {
    return (
        <div className="App">
            <div className="container">
                <h1>Loser!</h1>
                <p>
                    <button className="btn-info" onClick={reset}>
                        New Game
                    </button>
                </p>
            </div>
        </div>
    );
}

function YouWon({reset}) {
    return (
        <div className="App">
            <div className="container">
                <h1>Congrats! You Won! :)</h1>
                <p>
                    <button className="btn-info" onClick={reset}>
                        New Game
                    </button>
                </p>
            </div>
        </div>
    );
}

function App() {

    const [code, setCode] = useState(getRandomCode());
    const [guesses, setGuesses] = useState([]);
    const [number, setNumber] = useState("");
    const [dispError, setDispError] = useState("");

    let w = win(guesses, code);
    let results = getResults(guesses, code);
    let e = getError(number, guesses);
    let attempts = [1, 2, 3, 4, 5, 6, 7, 8];

    function guess() {
        let n = number;
        setNumber("");
        if (e === "") {
            let newGuesses = guesses.concat(n);
            setGuesses(newGuesses);
        }
        setDispError(e);
    }

    function newGame() {
        setGuesses([]);
        setCode(getRandomCode());
        //setNumber("");
        setDispError("");
    }

    //mostly from "updateText" in lecture notes
    function updateNumber(ev) {
        let val = ev.target.value;
        if (val.length > 4) {
            val = val.slice(0, 4);
        }
        setNumber(val);
    }

    function keyUp(ev) {
        if (ev.key === "Enter") {
            guess();
        }
    }

    if (w) {
        return <YouWon reset={newGame}/>;
    }

    if (guesses.length >= 8) {
        return <YouLost reset={newGame}/>
    }

    return (
        <div className="App">
            <div className="container">
                <h1>4Digit Game!</h1><br/>
                <p>
                    <button className="btn-info" onClick={newGame}>New Game</button>
                    <br/><br/>
                </p>
                <div className="centered">
                    <p>Try to guess the secret code! Make a guess, you have eight tries to figure it out!</p>
                    <p>Bulls are right numbers in the right place. Cows are right numbers in the wrong place.</p>
                    <br/><br/>
                </div>
                <div className="row">
                    <div className="col-sm-4"></div>
                    <div className="col">
                        <p>Enter 4 digits to make a guess:</p>
                        <input type="text" value={number} onChange={updateNumber} onKeyUp={keyUp}/>
                        <button className="btn-info" onClick={guess}>Guess!</button>
                        <p>{dispError}</p>
                    </div>
                    <div className="col-sm-1">
                        <div className="new-line">
                            {attempts.join("\n")}
                        </div>
                    </div>
                    <div className="col-sm-1">
                        <div className="new-line">
                            {guesses.join("\n")}
                        </div>
                    </div>
                    <div className="col-sm-2">
                        <div className="new-line">
                            {results.join('\n')}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
