import './App.css';
import {useState} from 'react';
import {getRandomCode, getError, getResults, win} from "./game";
import 'lodash';
import 'milligram';

function YouLost({reset}) {
    return (
        <div className="App">
            <h1>Loser!</h1>
            <p>
                <button onClick={reset}>
                    New Game
                </button>
            </p>
        </div>
    );
}

function YouWon({reset}) {
    return (
        <div className="App">
            <h1>Congrats! You Won! :)</h1>
            <p>
                <button onClick={reset}>
                    New Game
                </button>
            </p>
        </div>
    );
}

function App() {

    const [code, setCode] = useState(getRandomCode());
    const [guesses, setGuesses] = useState([]);
    const [number, setNumber] = useState("");

    let w = win(guesses, code);
    let results = getResults(guesses, code);
    let e = getError(number, guesses);

    function guess() {
        console.log(code);
        let n = number;
        setNumber("");
        if (e === "") {
            let newGuesses = guesses.concat(n);
            setGuesses(newGuesses);
            console.log(e);
        }
    }

    function newGame() {
        setGuesses([]);
        setCode(getRandomCode());
        setNumber("");
    }

    // mostly from "updateText" in lecture notes
    function updateNumber(ev) {
        if (number.length < 4) {
            let d = ev.target.value;
            let num = d[d.length - 1];
            setNumber(number + num);
        }
    }

    function keyDown(ev) {
        console.log(ev.key);
        if (ev.key === "Enter") {
            guess();
        } else if (ev.key === "Backspace") {
            let num = number.substring(0, (number.length - 1));
            setNumber(num);
        }
    }

    if (w) {
        return <YouWon reset={newGame}/>;
    }

    if (guesses.length >= 8) {
        return <YouLost reset={newGame}/>
    }

    return (
        <div className="App">
            <h1>4Digit Game!</h1>
            <p>
                <button className="button-outline" onClick={newGame}>New Game</button>
            </p>
            <div className="centered">
                <p>Try to guess the secret code! Make a guess, you have eight tries to figure it out!</p>
                <p>Bulls are right numbers in the right place. Cows are right numbers in the wrong place.</p>
            </div>
            <div className="column-50 column-offset-25">
                <div className="row">
                    <div className="column">
                        <p>Enter 4 digits to make a guess:</p>
                    </div>
                    <div className="column">

                        <p>Your guesses:</p>
                    </div>
                </div>
                <div className="row">
                    <div className="column column-25 column-offset-25">
                        <p>
                            <input type="text" value={number} onChange={updateNumber} onKeyDown={keyDown} />
                            <button onClick={guess}>Guess!</button>
                        </p>
                        <p>{e}</p><br/>
                    </div>
                    <div className="column new-line">
                        {guesses.join("\n")}
                    </div>
                    <div className="column new-line">
                        {results.join('\n')}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
